//Importando o React
import React, { Component } from 'react';
import { Button, Modal} from 'react-materialize';

const API_URL = 'https://api-fullstackgo.herokuapp.com/api';

class Add extends Component {
    state = {
        investments: []
    }
    componentDidMount() {
        const url = `${API_URL}/details/`;
        axios.get(url).then(response => response.data)
        .then((data) => {
          this.setState({ investments: data })
          console.log(this.state.investments)
        });

    }


    render() {
        return (
           <Modal
                actions={[
                    <Button flat modal="close" node="button" className="btnGr">Fechar</Button>,
                    <Button flat modal="submit" node="button" className="btnYe">Adicionar</Button>
                ]}
                bottomSheet={false}
                //fixedFooter
                id="Modal-0"
                open={false}
                options={{
                    dismissible: true,
                    endingTop: '10%',
                    inDuration: 250,
                    onCloseEnd: null,
                    onCloseStart: null,
                    onOpenEnd: null,
                    onOpenStart: null,
                    opacity: 0.5,
                    outDuration: 250,
                    preventScrolling: true,
                    startingTop: '4%'
                }}
                //root={[object HTMLBodyElement]}
                trigger={
                    <div className="fixed-action-btn">
                        <a className="btn-floating btn-large light-blue accent-3" src={Add}>
                        <i className="large material-icons">add</i>
                        </a>
                    </div>
                }
                >
                <form>
                    <p className="center-align tit">adicione um investimento:</p>
                    <label>
                        Tipo:
                        <select className="browser-default mbForm mtForm">
                            <option value="" disabled selected>Selecione</option>
                            <option value="1">Renda Fixa</option>
                            <option value="2">Renda Vari√°vel</option>
                        </select>
                    </label>
                    <label>
                        Valor:
                        <input className='padInput mbForm mtForm' type="text" placeholder="Valor" name="name"/>
                    </label>
                    <label>
                        Data:
                        <input className='padInput mtForm' type="date" placeholder="Valor" name="name"></input>
                    </label>
                    <button type="submit">Create Post</button>
                </form>

            </Modal>
        )
    }
};

export default Add;
